$.fn.prefixCSS = function(property, value) {
	var prefixes = ["-webkit-","-moz-", "-ms-", "-o-"];
	for (var i = 0; i < this.length; i++) {
		var node = this[i];
		for (var k = 0; k < prefixes.length; k++) {
			var cssProperty = prefixes[k] + property;
			$(node).css(cssProperty, value);
		}

		// set the property without prefix as well
		$(node).css(property, value);
	}

	return this;
}

function PaintJS(config) {
	if (!window.jQuery  || jQuery  == undefined) return {error: "Using PaintJS requires jQuery.js"};
	if (!window.jscolor || jscolor == undefined) return {error: "Using PaintJS requires JSColor.js"};
	this.paintContainer = null;

	if (typeof config.paint == "string") {
		var node = document.querySelector(config.paint);
		if (node) this.paintContainer = node;
	} else if (typeof config.paint == "object") {
		this.paintContainer = config.paint;
	}

	var allowedKeys = [
		"brushSize",
		"brushColor",
		"paletteColors",
		"brushes",
		"brush",
		"maxStages"
	];

	for (var key in config) {
		if (allowedKeys.indexOf(key) > -1) {
			this[key] = config[key];
		}
	}

	this.init();
}

PaintJS.prototype = {
	isBeingResized: false,
	resizingOptions: {
		x: 0, // x coordinate of point where the mouse was at the beginning of resizing
		y: 0, // y
		resizer: {
			width: 0, // how much width has to be removed from canvas on resize
			height: 0 // how much height has to be removed from canvas on resize
		},
		beforeResizing: {
			width: 0, // canvas width before resizing started
			height: 0
		}
	},
	defaultPaletteColors: [
		"#000000",
		"#0000FF",
		"#00FF00",
		"#00FFFF",
		"#800080",
		"#808080",
		"#ff0000",
		"#FF00FF",
		"#ffff00",
		"#ffffff"
	],
	zoom: 100,
	maxStages: 100,
	// this canvas is used for resizing issues,
	// when resizing canvas it gets cleared so we have to keep track of canvas before it is being resized
	// and redraw it after it is done
	fakeCanvas: document.createElement("canvas"),
	initialSize: {
		height: 0,
		width: 0
	},
	createResizeButtons: function() {
		var width  = 8;
		var height = 8;

		var resizeRight       = document.createElement("div");
		var resizeBottom      = document.createElement("div");
		var resizeRightBottom = document.createElement("div");

		var buttons = [resizeRight, resizeBottom, resizeRightBottom];

		$(buttons).css({
			"position": "absolute",
			"height": "5px",
			"width": "5px",
			"background": "black",
			"border": "1px solid lightgray",
			"left": "calc(50% - 10px)",
			"top": "calc(50% - 10px)",
			"z-index": "2"
		});

		$(resizeRight).css({
			"right": "0",
			"left": "auto",
			"cursor": "ew-resize"
		});
		$(resizeBottom).css({
			"bottom": "0",
			"top": "auto",
			"cursor": "ns-resize"
		});
		$(resizeRightBottom).css({
			"right": "0",
			"bottom": "0",
			"top": "auto",
			"left": "auto",
			"cursor": "nwse-resize"
		});

		for (var i = 0; i < buttons.length; i++) {
			buttons[i].isResizer = true; // it's just prettier to use it like this
		};

		resizeRight.resizerInfo = {
			x: 1,
			y: 0
		};

		resizeBottom.resizerInfo = {
			x: 0,
			y: 1
		};

		resizeRightBottom.resizerInfo = {
			x: 1,
			y: 1
		}

		return buttons;
	},
	fromURL: function(url) {
		var paintObject = this;
		return new Promise(function(resolve, reject) {
			if (!url) {
				reject("You have to provide URL argument for fromURL method");
				return;
			}

			var img = new Image();
			img.crossOrigin = "anonymous";

			img.onload = function() {
				// var contentType = xhr.getResponseHeader("Content-Type");
				paintObject.canvas.width = img.width;
				paintObject.canvas.height = img.height;
				paintObject.clear();
				paintObject.ctx.drawImage(this, 0, 0);

				paintObject.initialSize = {
					height: paintObject.canvas.height,
					width: paintObject.canvas.width
				};

				resolve(paintObject);
			};

			img.onerror = function(err) {
				reject(err);
			}

			img.src = url;
		});
	},
	clear: function() {
		this.ctx.clearRect(0, 0, this.canvas.width, this.canvas.height);
	},
	createPaletteButton: function(color) {
		var button = document.createElement("div");

		if (!this.isHexColor(color)) {
			console.error("Could not createColorButton() from given color - '" + color + "' since it is not a valid hexadecimal color value. It has to start from # and has to be 3 or 6 characters length");
			return false;
		}

		$(button).height(32).width(32).css({
			"background": color,
			"float": "left"
		});

		button.colorValue = color;
		button.paintJS    = this;
		button.onclick    = function() {
			this.paintJS.setColorFromButton(this);
		}

		return button;
	},
	setColorFromButton: function(button) {
		this.setColor(button.colorValue);
	},
	setColor: function(color) {
		if (this.isHexColor(color)) {
			this.currentColor.jscolor.fromString(color);
			this.currentColor.onchange();
		} else {
			console.log("Please use hexadecimal value (#ffffff or #fff) while using setColor");
		}
	},
	setBrush: function(brush) {
		var brushObject = brush.paintJSBrush;
		this.brush = brushObject;

		$(this.brushesContainer.children).each(function(index, el) {
			$(el).css({
				"border": "none",
				"padding": "5px"
			});
		});

		$(brush).css({
			"border": "1px solid rgba(0, 0, 0, 0.2)",
			"padding": "4px"
		});
	},
	initialBrushes: function() {
		var defaultBrush = new PaintJSBrush({
			icon: "data:image/png;base64,",
			brushIcon: "data:image/png;base64,",
			previous: {
				x: 0,
				y: 0
			},
			draw: function(x, y) {
				var ctx        = this.paintJS.ctx;
				var brushColor = this.paintJS.brushColor;

				ctx.beginPath();
				ctx.lineJoin    = "round";
				ctx.lineWidth   = this.paintJS.brushSize;
				ctx.strokeStyle = brushColor;

				ctx.moveTo(this.previous.x, this.previous.y);
				ctx.lineTo(x, y);
				ctx.closePath();
				ctx.stroke();
			},
			mousedown: function(e) {
				this.previous = {
					x: e.x - 0.5,
					y: e.y - 0.5
				};

				this.draw(e.x + 0.5, e.y);
				this.mouseIsDown = true;
			},
			mousemove: function(e) {
				if (this.mouseIsDown) {
					this.draw(e.x, e.y);

					this.previous = {
						x: e.x,
						y: e.y
					};
				}
			},
			mouseup: function(e) {
				if (this.mouseIsDown) {
					// if mousedown is true, the mouseup meant that user had drawn something
					this.paintJS.canvasHistory.addStage();
				}
				
				this.mouseIsDown = false;
			}
		});

		var fillBrush = new PaintJSBrush({
			icon: "data:image/png;base64,",
			brushIcon: "data:image/png;base64,",
			// returns object containing RGBA values
			// from array of image data and index of first pixel
			getRGBA: function(array, index) {
				return {
					r: array[index],
					g: array[index + 1],
					b: array[index + 2],
					a: array[index + 3]
				};
			},
			isSameColor: function(color1, color2) {
				return color1.r === color2.r &&color1.g === color2.g && color1.b === color2.b && color1.a === color2.a;
			},
			mousedown: function(e) {
				var canvasWidth = this.paintJS.canvas.width;
				var canvasHeight = this.paintJS.canvas.height;

				// get canvas pixels
				var imageData = this.paintJS.ctx.getImageData(0, 0, canvasWidth, canvasHeight);
				var pixels = imageData.data;

				var x = Math.floor(e.x);
				var y = Math.floor(e.y);

				var indexOfCurrentPixel = 4 * (y * canvasWidth + x);
				// color which has to be filled continuesly
				var initialColor = this.getRGBA(pixels, indexOfCurrentPixel);

				var brushColor = this.paintJS.brushColor.slice(1);
				brushColor = {
					r: parseInt(brushColor.slice(0, 2), 16),
					g: parseInt(brushColor.slice(2, 4), 16),
					b: parseInt(brushColor.slice(4, 6), 16),
					a: 255
				};

				if (this.isSameColor(initialColor, brushColor)) return;
				
				var queue = [
					[x, y]
				];

				var start = new Date().getTime();
				// while (queue.length) {
				// 	var cords = queue.pop();
				// 	var x = cords[0];
				// 	var y = cords[1];

				// 	var indexOfCurrentPixel = 4 * (y * canvasWidth + x);
				// 	var currentColor = this.getRGBA(pixels, indexOfCurrentPixel);
				// 	if (this.isSameColor(currentColor, initialColor)) {
				// 		// fill this pixel with initial color
				// 		pixels[indexOfCurrentPixel]     = brushColor.r;
				// 		pixels[indexOfCurrentPixel + 1] = brushColor.g;
				// 		pixels[indexOfCurrentPixel + 2] = brushColor.b;
				// 		pixels[indexOfCurrentPixel + 3] = brushColor.a;

				// 		if (x <= canvasWidth)  queue.push([x + 1, y]);
				// 		if (x > 0)             queue.push([x - 1, y]);
				// 		if (y <= canvasHeight) queue.push([x    , y + 1]);
				// 		if (y > 0)             queue.push([x    , y - 1]);
				// 	}
				// 	indexOfCurrentPixel = null;
				// }
				
				// the code above is actual code, but after testing i discovered that it is better not to create any other objects
				// like while using function getRGBA or using function like isSameColor creates extra references so
				// the code below is just kinda uglyfied and minified version of above
				// UPDATE: after testing, the code below works 2 times faster
				
				while (queue.length) {
					var cords = queue.pop();

					var indexOfCurrentPixel = 4 * (cords[1] * canvasWidth + cords[0]);
					
					if (initialColor.r == pixels[indexOfCurrentPixel] &&
						initialColor.g == pixels[indexOfCurrentPixel + 1] &&
						initialColor.b == pixels[indexOfCurrentPixel + 2]) {
						// fill this pixel with initial color
						pixels[indexOfCurrentPixel]     = brushColor.r;
						pixels[indexOfCurrentPixel + 1] = brushColor.g;
						pixels[indexOfCurrentPixel + 2] = brushColor.b;

						if (cords[0] < canvasWidth)  queue.push([cords[0] + 1, cords[1]]);
						if (cords[0] > 0)            queue.push([cords[0] - 1, cords[1]]);
						if (cords[1] < canvasHeight) queue.push([cords[0]    , cords[1] + 1]);
						if (cords[1] > 0)            queue.push([cords[0]    , cords[1] - 1]);
					}
					
					cords = null;
				}
				
				this.paintJS.ctx.putImageData(imageData, 0, 0);
				var timeTaken = (new Date().getTime() - start).toString();
				console.log("Fill brush took %s milliseconds", timeTaken);
				this.paintJS.canvasHistory.addStage();
			}
		});

		var eraserBrush = new PaintJSBrush({
			icon: "data:image/png;base64,",
			brushIcon: "data:image/png;base64,",
			erase: function(x, y) {
				var ctx        = this.paintJS.ctx;
				var radius     = this.paintJS.brushSize / 2;

				ctx.beginPath();
				ctx.lineJoin  = "round";
				ctx.lineWidth = radius;

				ctx.moveTo(this.previous.x, this.previous.y);
				ctx.lineTo(x, y);
				ctx.closePath();
				ctx.strokeStyle = "#fff";
				ctx.stroke();
			},
			mousedown: function(e) {
				this.previous = {
					x: e.x - 1,
					y: e.y - 1
				};

				this.erase(e.x, e.y);
				this.mouseIsDown = true;
			},
			mousemove: function(e) {
				if (this.mouseIsDown) {
					this.erase(e.x, e.y);
					this.previous = {
						x: e.x,
						y: e.y
					}
				}
			},
			mouseup: function() {
				if (this.mouseIsDown) {
					this.paintJS.canvasHistory.addStage();
				}
				
				this.mouseIsDown = false;
			}
		});

		var selectBrush  = new PaintJSBrush({
			icon: "data:image/png;base64,",
			brushIcon: ""
		});

		var eyedropperBrush = new PaintJSBrush({
			icon: "data:image/png;base64,",
			brushIcon: "data:image/png;base64,",
			getColor: function(x, y) {
				var ctx = this.paintJS.ctx;
				var imageData = ctx.getImageData(x, y, 1, 1).data;

				var r = imageData[0].toString(16);
				var g = imageData[1].toString(16);
				var b = imageData[2].toString(16);

				if (r.length == 1) r = "0" + r;
				if (g.length == 1) g = "0" + g;
				if (b.length == 1) b = "0" + b;

				return "#" + r + g + b;
			},
			mousedown: function(e) {
				this.paintJS.setColor(this.getColor(e.x, e.y));
				this.mouseIsDown = true; 
			},
			mousemove: function(e) {
				if (this.mouseIsDown) {
					this.paintJS.setColor(this.getColor(e.x, e.y));
				}
			},
			mouseup: function() {
				this.mouseIsDown = false;
			}
		})

		return [defaultBrush, fillBrush, eraserBrush, eyedropperBrush];
	},
	initBrushes: function() {
		var brushes = this.brushes;

		$(this.brushesContainer).remove();
		this.brushesContainer = document.createElement("div");

		var brushesContainer = this.brushesContainer;
		$(brushesContainer).empty().css({
			"margin": "4px 5px",
			"overflow": "hidden",
			"border": "1px solid rgba(0, 0, 0, 0.2)",
			"float": "right"
		}).appendTo(this.navbar);

		for (var i = 0; i < brushes.length; i++) {
			var brushObject = this.brushes[i];
			brushObject.paintJS = this;

			var brushDiv = document.createElement("div");
			var brushIcon = new Image(32, 32);
			brushIcon.src = brushObject.icon;

			$(brushDiv).append(brushIcon).appendTo(brushesContainer).css({
				"margin": "5px",
				"padding": "5px",
				"float": "left"
			})

			brushDiv.paintJS = this;
			brushDiv.paintJSBrush = brushObject;

			brushDiv.onclick = function() {
				this.paintJS.setBrush(this);
			}

			if (i == 0) {
				brushDiv.click();
			}
		}
	},
	initCanvas: function() {
		if (this.canvas) {
			console.error("This PaintJS object's canvas is already initialized.");
			return false;
		}

		var paintContainer = this.paintContainer;
		if (!paintContainer) {
			console.error("Could not initCanvas() since canvas container is not set. Please set canvas using PaintJS.canvas = canvas (where canvas is node or selector string)");
			return false;
		}

		var canvasContainer = document.createElement("div");
		$(canvasContainer).css({
			"background": "gray",
			"width": "calc(100% - 40px)",
			"height": "calc(100% - 114px)", // navbar height = 64px, navbar paddings = 10px, container paddings = 40px
			"padding": "20px",
			"min-height": "200px",
			"float": "left",
			"overflow": "auto"
		});

		var canvasWrapper = document.createElement("div");
		$(canvasWrapper).css({
			"position": "relative",
			"display": "inline-block",
			"cursor": "none",
			"border": "1px solid gray",
			"overflow": "hidden",
			"background": "white" // so that if user resizes it to bigger size than it was, the blank space will be white
		});
		var resizeButtons = this.createResizeButtons();
		$(canvasWrapper).append(resizeButtons);

		var brush = document.createElement("div");
		$(brush).css({
			"background": "url(" + this.brush.brushIcon + ")",
			"background-size": "cover",
			"height": (this.brushSize || 16) + "px",
			"width":  (this.brushSize || 16) + "px",
			"position": "absolute",
			"top": "0",
			"left": "0",
			"display": "none",
			"z-index": "1"
		});
		this.brushNode     = brush;
		brush.paintJSBrush = this.brush;
		brush.paintJS      = this; // just in case

		var canvas     = document.createElement("canvas");
		canvas.paintJS = this;
		canvas.width   = $(paintContainer).width() / 2;
		canvas.height  = $(paintContainer).height() / 2;
		
		canvas.style.display        = "block";
		canvas.style.imageRendering = "pixelated"; // when zoom is applied to canvas, browser tries to make it "smooth". this value will make sure that it's still pixelated

		this.canvas          = canvas;
		this.ctx             = canvas.getContext("2d");
		this.ctx.fillStyle   = "#fff";
		this.ctx.fillRect(0, 0, canvas.width, canvas.height);
		
		this.canvasHistory   = new CanvasHistory(canvas);
		this.canvasHistory.maxStages = this.maxStages;

		this.initialSize = {
			height: this.canvas.height,
			width: this.canvas.width
		};

		var navbar = document.createElement("div");
		$(navbar).css({
			"float": "left",
			"height": "64px",
			"padding": "5px",
			"width": "calc(100% - 10px)",
			"background": "rgb(213, 218, 214)"
		});
		this.navbar = navbar;

		var currentColor            = document.createElement("input");
		currentColor.className      = "jscolor {hash: true}";
		currentColor.value          = this.brushColor;

		$(currentColor).css({
			"font-size": "0",
			"padding": "0",
			"border": "none",
			"margin": "16px 5px",
			"float": "right"
		})

		currentColor.onchange = function() {
			this.paintJS.brushColor = this.value;
		}

		currentColor.paintJS      = this;
		currentColor.style.height = "32px";
		currentColor.style.width  = "32px";

		this.currentColor         = currentColor;

		var palette = document.createElement("div");
		for (var i = 0; i < this.paletteColors.length; i++) {
			var paletteButton = this.createPaletteButton(this.paletteColors[i]);
			if (paletteButton) {
				palette.appendChild(paletteButton);
			}
		}
		this.paletteContainer = palette;

		// if the count of palette colors is odd, the last piece gets wrapped
		var paletteWidth = palette.children.length % 2 === 0 ? palette.children.length * 16 : (palette.children.length + 1) * 16;
		$(palette).css({
			"width": paletteWidth + "px",
			"height": "64px",
			"overflow": "overlay",
			"margin": "0 5px",
			"float": "right"
		});

		var zoomContainer = document.createElement("div");
		$(zoomContainer).css({
			"border": "1px solid rgba(0, 0, 0, 0.2)",
			"float": "right",
			"margin": "5px"
		});

		var zoomIn = document.createElement("div");
		var zoomOut = document.createElement("div");
		var zoomButtons = [zoomIn, zoomOut];

		$(zoomButtons).css({
			"height": "32px",
			"width": "32px",
			"margin": "5px",
			"padding": "5px",
			"border": "1px solid rgba(0, 0, 0, 0.2)",
			"float": "left"
		}).appendTo(zoomContainer);
		$(zoomButtons).append("<img height='32px' width='32px' />");

		zoomIn.children[0].src = "data:image/png;base64,";
		zoomOut.children[0].src = "data:image/png;base64,";

		zoomIn.paintJS = this;
		zoomOut.paintJS = this;

		$(zoomIn).click(function() {
			var zoom = this.paintJS.zoom;
			zoom *= 2;
			if (zoom >= 1600) zoom = 1600;
			this.paintJS.zoom = zoom;
		});

		$(zoomOut).click(function() {
			var zoom = this.paintJS.zoom;
			zoom /= 2;
			if (zoom <= 25) zoom = 25;
			this.paintJS.zoom = zoom;
		});

		this.zoom = 100;
		
		var brushSizeChanger = $(
		'<div class="brush-size-changer">' +
			'<input type="range" class="brush-size-changer-dragger" />' +
			'<input type="number" class="brush-size-changer-value" />' +
		'</div>'
		);
		
		brushSizeChanger.css({
			"float": "left",
			"margin-top": "16.5px",
			"border": "1px solid rgba(0, 0, 0, 0.2)",
			"padding": "5px",
			"background": "rgba(255, 255, 255, 0.5)"
		});
		
		brushSizeChanger.find("input").css({
			"float": "left",
			"display": "block",
			"height": "22px",
			"box-sizing": "border-box"
		}).prop("paintJS", this);
		
		brushSizeChanger.find(".brush-size-changer-dragger").prop("max", 800).on("input", function() {
			$(this).parent().find(".brush-size-changer-value").val(this.value);
			this.paintJS.brushSize = this.value;
		}).css({
			"width": "100px",
			"margin": "0 5px"
		})
		
		brushSizeChanger.find(".brush-size-changer-value").css({
			"width": "50px"
		}).on("input", function() {
			$(this).parent().find(".brush-size-changer-dragger").val(this.value);
			this.paintJS.brushSize = this.value;
		});
		
		brushSizeChanger.find(".brush-size-changer-value").val(this.brushSize);
		brushSizeChanger.find(".brush-size-changer-dragger").val(this.brushSize);

		$(navbar).append(palette, currentColor);
		this.initBrushes();
		$(navbar).append(zoomContainer, brushSizeChanger);
		$(canvasWrapper).append(canvas, brush);
		$(canvasContainer).append(canvasWrapper);
		$(paintContainer).append(navbar, canvasContainer);
	},
	init: function() {
		if (!this.paintContainer) {
			var paintContainer = document.createElement("div");
			document.body.appendChild(paintContainer);
			this.paintContainer = paintContainer;
		}

		$(this.paintContainer).prefixCSS("user-select", "none");
		this.paintContainer.paintJS = this;

		this.brushSize     = this.brushSize     || 8;
		this.brushColor    = this.brushColor    || "#000000";
		this.paletteColors = this.paletteColors || this.defaultPaletteColors;
		this.brushes       = this.brushes       || this.initialBrushes();

		if (this.brush) {
			this.brushes.unshift(brush);
		} else {
			this.brush = this.brushes[0];
		}

		this.initCanvas();

		var object = this;

		$(document).mousedown(function(e) {
			if (e.button == 0) {
				object.mousedown(e);
			} else {
				object.rightMousedown(e);
			}
		});

		$(document).mousemove(function(e) {
			object.mousemove(e);
		});

		$(document).mouseup(function(e) {
			if (e.button == 0) {
				object.mouseup(e);
			} else {
				object.rightMouseup(e);
			}
		});
		
		$(document).contextmenu(function(e) {
			object.contextMenu(e);
		});
		
		$(document).keydown(function(e) {
			object.keydown(e);	
		});
		
		$(document).keyup(function(e) {
			object.keyup(e);	
		});
		
		$(document).keypress(function(e) {
			object.keypress(e);
		})
	},
	isHexColor: function(hex) {
		return hex.indexOf("#") === 0 // starts with #
		&& (hex.length === 4 || hex.length === 7) // is #fff or #ffffff
		&& !isNaN(parseInt(hex.slice(1), 16)); // the content without # has to be valid hexadecimal number
	},
	mousedown: function(e) {
		var target = e.target;
		if (target.isResizer) {
			this.isBeingResized = true;
			this.resizingOptions = {
				x: e.pageX,
				y: e.pageY,
				resizer: target.resizerInfo,
				beforeResizing: {
					width: this.canvas.width,
					height: this.canvas.height
				}
			};

			this.fakeCanvas.width  = this.canvas.width;
			this.fakeCanvas.height = this.canvas.height;
			var ctx = this.fakeCanvas.getContext("2d");
			ctx.drawImage(this.canvas, 0, 0);

			this.canvas.parentElement.style.border = "1px dotted black";
		} else {
			if (target == this.brushNode) {
				var offset = $(this.brushNode.parentElement).offset();
				var y = e.pageY - offset.top;
				var x = e.pageX - offset.left;

				this.brushNode.paintJS.brush.mousedown({
					x: x / this.zoom * 100,
					y: y / this.zoom * 100
				});
			}
		}
	},
	mousemove: function(e) {
		if (this.isBeingResized) {
			var resizingOptions = this.resizingOptions;

			var prevX = resizingOptions.x;
			var prevY = resizingOptions.y;

			var currX = e.pageX;
			var currY = e.pageY;

			var resizeH = (currY - prevY) * resizingOptions.resizer.y; // if y == 0, it wont resize canvas in height
			var resizeW = (currX - prevX) * resizingOptions.resizer.x;

			var newWidth = this.resizingOptions.beforeResizing.width + resizeW;
			var newHeight = this.resizingOptions.beforeResizing.height + resizeH;

			if (newWidth < 10)  newWidth  = 10;
			if (newHeight < 10) newHeight = 10;

			var canvasWrapper = this.canvas.parentElement;
			$(canvasWrapper).height(newHeight).width(newWidth);
		} else {
			if (!e.target.isResizer) {
				var offset = $(this.brushNode.parentElement).position();
				var top    = e.pageY - offset.top;
				var left   = e.pageX - offset.left;

				$(this.brushNode).show().css({
					"top": (top - $(this.brushNode).height() / 2) + "px",
					"left": (left - $(this.brushNode).width() / 2) + "px"
				});

				if (left >= $(this.canvas).width()) left = $(this.canvas).width();
				if (top >= $(this.canvas).height()) top = $(this.canvas).height();
				if (left < 0) left = 0;
				if (top < 0) top = 0;

				this.brush.mousemove({
					x: left / this.zoom * 100,
					y: top / this.zoom * 100
				});

				offset = null;
				top    = null;
				left   = null;
			} else {
				$(this.brushNode).hide();
			}
		}
	},
	mouseup: function(e) {
		if (this.isBeingResized) {
			this.isBeingResized = false;

			var canvasWrapper = this.canvas.parentElement;
			$(canvasWrapper).css("border", "1px solid gray");

			var newWidth  = $(canvasWrapper).width();
			var newHeight = $(canvasWrapper).height();

			this.canvas.width  = newWidth;
			this.canvas.height = newHeight;

			this.clear();
			if (newWidth > this.fakeCanvas.width || newHeight > this.fakeCanvas.height) {
				// if the canvas was resized so that it increased in height or width, we have to fill it with white first
				this.ctx.fillStyle = "#fff";
				this.ctx.fillRect(0, 0, newWidth, newHeight);
			}
			this.ctx.drawImage(this.fakeCanvas, 0, 0);

			// reset the canvas wrapper height and width so it wraps around canvas instead of getting stuck at current size
			var canvasWrapper = $(this.canvas.parentElement);
			canvasWrapper.css({
				"height": "auto",
				"width": "auto"
			});
		}

		this.brush.mouseup({
			x: (e.pageX - $(this.brushNode.parentElement).offset().left) / this.zoom * 100,
			y: (e.pageY - $(this.brushNode.parentElement).offset().top) / this.zoom * 100
		});
	},
	rightMousedown: function(e) {
		e.preventDefault();
	},
	rightMouseup: function(e) {
		e.preventDefault();
	},
	contextMenu: function(e) {
		e.preventDefault();
	},
	keydowns: {},
	keycodes: {
		 "13" : "ENTER",
		 "8"  : "BACKSPACE",
		 "219": "SQUARE_BRACKET_LEFT",
		 "221": "SQUARE_BRACKET_RIGHT",
		 "16" : "SHIFT",
		 "17" : "CTRL",
		 "18" : "ALT",
		 "91" : "CMD",
	},
	shortcuts: {
		// all of shortcut functions are colled in PaintJS object scope
		// so using this keyword will refer to PaintJS object
		"SQUARE_BRACKET_LEFT": function() {
			var decrementValue = 1;
			if (this.brushSize > 10) decrementValue = 5;
			if (this.brushSize > 50) decrementValue = 10;
			if (this.brushSize > 100) decrementValue = 25;
			if (this.brushSize > 200) decrementValue = 50;
			if (this.brushSize > 300) decrementValue = 100;
			
			this.brushSize -= decrementValue;
			
			// so that brush's center stays at same position
			var position = $(this.brushNode).position();
			$(this.brushNode).css({
				top: Math.floor(position.top) + decrementValue / 2,
				left: Math.floor(position.left) + decrementValue / 2
			});
		},
		"SQUARE_BRACKET_RIGHT": function() {
			var incrementValue = 1;
			if (this.brushSize > 10) incrementValue = 5;
			if (this.brushSize > 50) incrementValue = 10;
			if (this.brushSize > 100) incrementValue = 25;
			if (this.brushSize > 200) incrementValue = 50;
			if (this.brushSize > 300) incrementValue = 100;
			
			this.brushSize += incrementValue;
			
			// so that brush's center stays in the same position
			var position = $(this.brushNode).position();
			$(this.brushNode).css({
				top: Math.floor(position.top) - incrementValue / 2,
				left: Math.floor(position.left) - incrementValue / 2
			});
		},
		// undo
		"CMD+Z": function() {
			this.canvasHistory.restoreFromPreviousStage();	
		},
		"CTRL+Z": function() {
			this.canvasHistory.restoreFromPreviousStage();
		},
		// redo
		"CMD+Y": function() {
			this.canvasHistory.restoreFromNextStage();
		},
		"CTRL+Y": function() {
			this.canvasHistory.restoreFromNextStage();
		},
	},
	registerShortcut: function(shortcut, callback) {
		if (typeof shortcut != "string" || typeof callback != "function") return;
		
		this.shortcuts[shortcut] = callback;
	},
	deleteShortcut: function(shortcut) {
		return !!this.shortcuts[shortcut] && delete this.shortcuts[shortcut];
	},
	keydown: function(e) {
		var keyCode = e.keyCode || e.which;
		this.keydowns[keyCode] = true;
		
		var keydownNames = [];
		for (var keydownCode in this.keydowns) {
			if (this.keydowns[keydownCode] === false) continue;
			// if current keycode is stored inside keycodes object, use it to create shortcut string
			// otherwise try to create a string from char code
			var keyCodeValue = this.keycodes[keydownCode] || String.fromCharCode(parseInt(keydownCode));
			keydownNames.push(keyCodeValue);
		}
		
		// shortcut string created by current keydowns key names
		var currentShortcut = keydownNames.sort().join("+");
		
		for (var shortcut in this.shortcuts) {
			// sort shortcut key so that it matches the string above
			// if shortcut is CMD+SHIFT+R
			// and the shortcut is registered as CMD+R+SHIFT
			// they will still match because string will be re-sorted
			shortcut = shortcut.split("+").sort().join("+");
			if (shortcut == currentShortcut) {
				e.preventDefault();
				this.shortcuts[shortcut].bind(this)();
			}
		}
	},
	keyup: function(e) {
		e.preventDefault();
		this.keydowns = {};
	},
	keypress: function(e) {
		delete this.keydown[e.keyCode || e.which];
	}
}

Object.defineProperties(PaintJS.prototype, {
	"brushColor": {
		get: function() {
			return this._brushColor;
		},
		set: function(brushColor) {
			if (this.isHexColor(brushColor)) {
				this._brushColor = brushColor;
			} else {
				console.log("Please use hexadecimal value (#ffffff or #fff) while setting brushColor");
			}
		}
	},
	"brushSize": {
		get: function() {
			return this._brushSize;
		},
		set: function(brushSize) {
			brushSize = parseInt(brushSize) || 1; // to prevent brushSize from being 0 or any other thing
			this._brushSize = brushSize;
			
			brushSize = brushSize * this.zoom / 100; // increase the brush size as well

			$(this.brushNode).css({
				"height": brushSize + "px",
				"width":  brushSize + "px"
			});
		}
	},
	"paletteColors": {
		get: function() {
			return this._paletteColors;
		},
		set: function(paletteColors) {
			// removed check of at least one element in array
			// since there can be no color shortcuts at all
			this._paletteColors = paletteColors || [];
		}
	},
	"brush": {
		get: function() {
			return this._brush;
		},
		set: function(brush) {
			if (brush instanceof PaintJSBrush) {
				this._brush = brush;
				$(this.brushNode).css("background-image", "url(" + brush.brushIcon + ")");
				this.brushSize = this.brushSize; // just to resize the brush again
			}
		}
	},
	"zoom": {
		get: function() {
			return this._zoom;
		},
		set: function(zoom) {
			this._zoom = zoom;

			var newHeight = this.initialSize.height * zoom / 100;
			var newWidth = this.initialSize.width * zoom / 100;

			if (zoom == 100) {
				this.canvas.style.width = "auto";
				this.canvas.style.height = "auto";
			} else {
				this.canvas.style.width = newWidth + "px";
				this.canvas.style.height = newHeight + "px";
			}
			
			this.brushSize = this.brushSize; // re calculate brush size because zoom changed
		}
	}
});

function PaintJSBrush(config) {
	config = config || {};
	if (!config.icon) return {};
	if (!config.brushIcon) return {};

	for (var conf in config) {
		this[conf] = config[conf];
	}

	this.mousedown = config.mousedown || function() {};
	this.mousemove = config.mousemove || function() {};
	this.mouseup   = config.mouseup   || function() {};
};

function CanvasHistory(canvas) {
	if (!canvas || canvas.nodeName != "CANVAS")  return {};
	this.canvas = canvas;
	this.ctx    = canvas.getContext("2d");
	this.addStage();
}

CanvasHistory.prototype = {
	historyArray: [],
	maxStages: 100, // maximum amount of stages
	currentStage: 0,
	// pushes current state of canvas to historyArray
	addStage: function() {
		var stage = {
			width: this.canvas.width,
			height: this.canvas.height,
			imageData: this.ctx.getImageData(0, 0, this.canvas.width, this.canvas.height)
		};
		
		if (this.currentStage != this.historyArray.length - 1) {
			this.historyArray.splice(this.currentStage + 1); // delete history after this stage
		}
		
		this.historyArray.push(stage);
		
		if (this.historyArray.length > this.maxStages) {
			this.historyArray.shift(); // remove last item (which is first in array)
		}
		
		this.currentStage = this.historyArray.length - 1;
	},
	// restores canvas from previous stage (using stage index as an argument)
	restoreFromStage: function(stageIndex) {
		var stage = this.historyArray[stageIndex];
		if (stage) {
			this.currentStage  = stageIndex;
			this.canvas.width  = stage.width;
			this.canvas.height = stage.height;
			this.ctx.putImageData(stage.imageData, 0, 0);
		}
	},
	// kind of an undo command
	restoreFromPreviousStage: function() {
		this.restoreFromStage(this.currentStage - 1);
	},
	// kind of a redo command
	restoreFromNextStage: function() {
		this.restoreFromStage(this.currentStage + 1);
	}
}